sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if (isLoading) {
		return;
	}

	var businessUnit = g_form.getValue('business_unit');

	// consumer affairs options
	if (newValue === 'attorney' ||
		newValue === 'bbb' ||
		newValue === 'ethics_and_integrity' ||
		(newValue === 'other' && businessUnit === 'consumer_affairs')) {


		if (g_form.getValue('due_date') === '') {

			var ga = new GlideAjax('EcmSlaDateUtils');
			ga.addParam('sysparm_name', 'getRegulatoryDueDate');
			ga.getXML(function(response) {

				var dueDate = response.responseXML.documentElement.getAttribute("answer");
				g_form.setValue('due_date', dueDate);
			});
		}
	}

	// regulatory affairs options
	else if (newValue === 'attorney_general' || 
			 newValue === 'doh' || 
			 newValue === 'dol' || 
			 newValue === 'external_review' ||
			 newValue === 'medical_society' || 
			 newValue ===  'misc_state_agency' ||
			 newValue ===  'ocr_regulatory_office' ||
			 newValue ===  'ombudsman' ||
			 newValue ===  'state_exchange' ||
			 (newValue ===  'other' && businessUnit == 'regulatory_affairs')) {

		if (g_form.getValue('due_date') === '') {

			var tenCalendarDaysOut = new Date();
			tenCalendarDaysOut.setDate(tenCalendarDaysOut.getDate() + 10);

			var userFormattedDate = formatDate(tenCalendarDaysOut, g_user_date_time_format);
			g_form.setValue('due_date', userFormattedDate);
		}
	}

	else {
		g_form.setValue('due_date', '');
	}
}
