sys_script_include.script:
var ECMIntegrations = Class.create();
ECMIntegrations.prototype = {
		/** V E R A C I T Y ~ www.meetveracity.com ~ **
		* Author: Todd Fudala ~ tfudala@meetveracity.com

		* Description: Class to manage integrations for the ECM application

		*/

		// CONSTANTS

		HTTPSTATUS_SUCCESS : "200",	// Indicates a successful HTTP post of a SOAP Message
		INSERT : "N",
		UPDATE : "X",	
		LOGSOURCE : "ECMIntegrations.", // Log Source
		LOGHEADER : "{0}{1} >[{2}]:\r\n{3}", // Standard prefix
		LOG_INFO : "INFO",
		LOG_ERROR : "ERROR",
		LOG_DEBUG : "DEBUG",
		LOG_EXAMPLE : "gs.error(this.LOGHEADER , this.LOGSOURCE , functName, this.LOG_ERROR, message);",
		
		initialize: function() {
			
		},
		
		/** V E R A C I T Y ~ www.meetveracity.com ~ **
 		* Author: Todd Fudala ~ tfudala@meetveracity.com
		
 		* Description: Outbound SOAP API call to ORS
		
 		* Parameters:
 		* glide_record: the current record
 		* insert_or_update_constant: this.INSERT or this.UPDATE
		
 		* Returns: True if the call is successful, False if unsucessful
 		*/
		
		insertORS : function(glide_record, insert_or_update_constant) {
			var functName = "insertORS()";
			
			var gr = new GlideRecord();
			gr = glide_record;	
			
			// If this is an UPDATE set the correct code;
			var insert_update = (insert_or_update_constant==this.UPDATE) ? 'X' : 'N'; 
			
			// If this is an UPDATE then we are closing the issue in ORS (resolved in ECM)
			var open_or_closed = (insert_or_update_constant==this.UPDATE) ? 'C' : 'O'; 
			
			// Find the group number
			var group_number = (gr.contact_is_member) ? gr.contact_group_number : gr.member_group_number; 
			
			
			try {
				var s = new sn_ws.SOAPMessageV2('x_opt_ecm.CreateOrUpdateIssueV13', 'invokeService');
				
				//override authentication profile
				//authentication type ='basic'
				//r.setAuthentication(authentication type,profile name);
				s.setStringParameterNoEscape('requestHeader.applicationInstanceName', 'ECM');
				s.setStringParameterNoEscape('requestHeader.applicationName', 'ECM');
				s.setStringParameterNoEscape('requestHeader.logLevel', 'DEBUG');
				s.setStringParameterNoEscape('orsSystemParameter.logonOffice', 'XXX');
				s.setStringParameterNoEscape('orsSystemParameter.logonId', 'XXX');
				
				s.setStringParameterNoEscape('member.memberNumber', group_number);
				s.setStringParameterNoEscape('memberName.firstName', gr.member_first_name);
				s.setStringParameterNoEscape('memberName.lastName', gr.member_last_name);
				s.setStringParameterNoEscape('dependentName.firstName', 'ECM');
				s.setStringParameterNoEscape('dependentName.lastName', 'TESTING');
				s.setStringParameterNoEscape('callerName.firstName', gr.contact_first_name);
				s.setStringParameterNoEscape('callerName.lastName', gr.contact_last_name);
				
				s.setStringParameterNoEscape('issue.status', open_or_closed);  // O(PEN) C(LOSED)
				s.setStringParameterNoEscape('issue.reasonCategory', '');
				s.setStringParameterNoEscape('issue.reasonCode', '');
				s.setStringParameterNoEscape('issue.reasonType', ''); // Leave blank per Manish Arora
				s.setStringParameterNoEscape('issue.callHistoryFlag', 'N'); // Default to N per Manish Arora
				s.setStringParameterNoEscape('issue.caseNumber', gr.number); // The case number
				s.setStringParameterNoEscape('issue.createdByApplicationIndicator', 'Q'); // Q==ECM application to ORS
				s.setStringParameterNoEscape('issue.directionCode', 'I'); //Incoming
				s.setStringParameterNoEscape('issue.updateType', insert_update); // This is N on initial and X on update
				//todo: map to transaction source / verbal source / written source ?
				s.setStringParameterNoEscape('issue.contactMethodCode', 'E'); // E – E-mail, F – Fax, M – Mail, P – Phone, W – Walk-in, I – Internet, T – IVR, V – In-house transfer
				s.setStringParameterNoEscape('issue.priority', 'E'); //Escalated
				//todo: map to contact relationship ?
				s.setStringParameterNoEscape('issue.originatorTypeCode', 'EM'); // MEMBER
				//todo: parse work notes and generate an array of <comments/>
				s.setStringParameterNoEscape('issue.comments', '<![CDATA[' + gr.intake_comments.getJournalEntry(-1) + ']]>'); // 71 byte limit per <comments/> tag - oh mainframes.
				
				var response = s.execute();
				var responseBody = response.getBody();
				var status = response.getStatusCode();
				
				// Validate the SOAP call was successful and log results
				if(this.orsPostAccepted(s,response, gr.number)){
					return true;
				}
				
				
			}
			catch(ex) {
				var message = ex.getMessage();
				gs.error(this.LOGHEADER , this.LOGSOURCE , functName, this.LOG_ERROR, message);
				return false;
			}
		},
		
		/** V E R A C I T Y ~ www.meetveracity.com ~ **
 		* Author: Todd Fudala ~ tfudala@meetveracity.com
		
 		* Description: Validates the ORS SOAP response and logs success and failure
		
 		* Parameters:
 		* soap: the original soap request message
 		* soapresponse: the soap response message
 		* number: the ECM case number this is regarding
		
 		* Returns: True if the call is successful, False if unsucessful
 		*/
		
		orsPostAccepted : function (soap, soapresponse, number)
		{
			var functName = "orsPostAccepted()";
			ret = false;
			var msg = '';
			
			try{
				
				var error_msg = soapresponse.haveError() ? soapresponse.getErrorMessage() : null;
				var body = soapresponse.getBody();
				var status = soapresponse.getStatusCode();
				var doc = new XMLDocument2();
				doc.parseXML(body); // Load XML
				var valid_doc = doc.isValid(); // Validate
				
				// First test for systemic success, then functional success in the ORS response
				if(status == this.HTTPSTATUS_SUCCESS && !soapresponse.haveError()) {
					if(!gs.nil(doc) && valid_doc){
						
						var fault_error = doc.getNodeText("//faultcode"); // Type of fault
						var fault_msg = doc.getNodeText("//faultstring"); // Fault description
						var ors_issue = doc.getNodeText("//issueId"); // This indicates success creating a new ORS Issue
						
						if(!gs.nil(fault_error)){
							throw new Error(fault_error + ":" + fault_msg); //Something in the API went wrong, log error
						}
						else if(gs.nil(ors_issue)){
							throw new Error("An ORS Issue ID was not returned for this call."); //Looks like success but something else went wrong, log error
						}
						else {
							var success_msg = "ORS Issue # " + ors_issue + " successfully created for ECM Case # :" + number; //Successful ORS Issue creation
							gs.info(this.LOGHEADER , this.LOGSOURCE , functName, this.LOG_INFO, success_msg);
						}
					}
				}
				else{
					throw new Error("The SOAP call failed, HTTP response code: >["
					+ status + "], SNC error code: [" + error_msg + "], SOAP response:\r\n " + body); //Systemic error
				}
				
				
				
			}
			catch(e){
				gs.error(this.LOGHEADER , this.LOGSOURCE , functName, this.LOG_ERROR, e.message);
			}
			return ret;
		},
		
		/** V E R A C I T Y ~ www.meetveracity.com ~ **
 		* Author: Todd Fudala ~ tfudala@meetveracity.com
		
 		* Description: Logs and returns the input for testing purposes
		
 		* Parameters:
 		* input: input message
		
 		* Returns: the input message
 		*/
		
		echo : function(input) {
			var functName = "echo()";
			gs.info(this.LOGHEADER , this.LOGSOURCE , functName, this.LOG_INFO, input);
			return input;
		},
		
		
		
		
		type: 'ECMIntegrations'
	};
