sys_script_include.script:
var CaseDocumentValidator = Class.create();
CaseDocumentValidator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	checkReadyForSubmit: function() {

		var returnMessage = 'ok';

		var caseSysId = this.getParameter('sysparm_case');

		var caseGR = new GlideRecord('x_opt_ecm_case');
		var foundCase = caseGR.get(caseSysId);
		if (!foundCase) {
			returnMessage = 'Could not locate Case';
		}
		else {
			var documentGR = new GlideRecord('dms_document');
			var foundDocument = documentGR.get(caseGR.getValue('audit_file'));
			if (!foundDocument) {
				returnMessage = 'Could not locate Audit File document';
			}
			else {

				var documentRevisionGR = new GlideRecord('dms_document_revision');
				documentRevisionGR.addQuery('document', documentGR.getUniqueValue());
				documentRevisionGR.query();

				var revisionCount = documentRevisionGR.getRowCount();
				if (revisionCount < 2) {
					returnMessage = 'Must have updated Audit file template';
				}
				else {
					while (documentRevisionGR.next()) {
						if (documentRevisionGR.checked_out_by != '') {
							returnMessage = 'Must check in all revisions';
						}
					}
				}
			}
		}

		gs.info('Checked Audit File status of {0}.  Result is : {1}', caseGR.getDisplayValue(), returnMessage);
		return returnMessage;
	},

    type: 'CaseDocumentValidator'
});
